def Storr(info, t2=True, god=''):
	text=info['originaltitle']
	url=httpSiteUrl+'/?q='+quote_plus(text.replace(' ','+'))#+'&p=2'
	http=GET(url,httpSiteUrl)
	http = decode_string(http)
	Lout=Parser(http, god)
	if Lout==[] and t2:
		text=info['title']
		url=httpSiteUrl+'/?q='+quote_plus(text.replace(' ','+'))#+'&p=2'
		http=GET(url,httpSiteUrl)
		Lout=Parser(http)
	return Lout



