def client(hash=None, log=None):
    from torrserve_stream import Engine

    if not hasattr(client, 'engine'):
        client.engine = Engine(
            host=ADDON.getSetting("remote_host"), 
            port=ADDON.getSetting("remote_port"))

    if hash:
        client.engine.hash = hash

    if log:
        client.engine.log = log

    return client.engine

class MyHandler(urllib2.HTTPHandler):
    def http_response(self, req, response):
        return response


def touch(url):
    o = urllib2.build_opener(MyHandler())
    t = threading.Thread(target=o.open, args=(url,))
    t.start()
